package thirdDayAssignment;

public class DataTypesPrint {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// Integer data type
		int a = 111;
		System.out.println("Interger Datatype: " + a);
		
		// Floating point data type
		float floatValue = 11.9f;	// 'f' is required to specify a float
		System.out.println("Float Datatype: " + floatValue);
		
		// Double data type 
		double myDouble = 20.99; 
        System.out.println("Double: " + myDouble);
		
        // Character type
        char myChar = 'A'; 
        System.out.println("Character: " + myChar);

        // Boolean type
        boolean myBoolean = true; 
        System.out.println("Boolean: " + myBoolean);

        // Byte type
        byte myByte = 127; // Range: -128 to 127
        System.out.println("Byte: " + myByte);

        // Short type
        short myShort = 32000; // Range: -32,768 to 32,767
        System.out.println("Short: " + myShort);

        // Long type
        long myLong = 9876543210L; // 'L' is required to specify a long
        System.out.println("Long: " + myLong);
        
        //String Datatype
        String myString = "Hello, Java!";
        System.out.println("String Value: "+ myString);


	}

}
